    private void btnExcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnExcluirActionPerformed
        // TODO add your handling code here:

        if(jtFormulario.getSelectedRow() != -1){

            tableModel.removeRow(jtFormulario.getSelectedRow());
        }
        else{
            JOptionPane.showMessageDialog(null,"Selecione um cadastro para excluir!");
        }
    }//GEN-LAST:event_btnExcluirActionPerformed

    private void btnAtualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAtualizarActionPerformed
        // TODO add your handling code here:

        if(jtFormulario.getSelectedRow() != -1){
            tableModel.setValueAt(txtRE.getText(),jtFormulario.getSelectedRow(),0);
            tableModel.setValueAt(txtNome.getText(),jtFormulario.getSelectedRow(),1);
            tableModel.setValueAt(txtCPF.getText(),jtFormulario.getSelectedRow(),2);
            tableModel.setValueAt(txtEmail.getText(),jtFormulario.getSelectedRow(),3);
            tableModel.setValueAt(txtCelular.getText(),jtFormulario.getSelectedRow(),4);
            tableModel.setValueAt(txtDataAdmissao.getText(),jtFormulario.getSelectedRow(),5);
            tableModel.setValueAt(selectAtuacao.getSelectedItem(),jtFormulario.getSelectedRow(),6);
        }
        else{
            JOptionPane.showMessageDialog(null,"Selecione um cadastro para atualizar!");
        }
    }//GEN-LAST:event_btnAtualizarActionPerformed

    private void btnCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCadastrarActionPerformed
        // TODO add your handling code here:

        DadosForm f = new DadosForm();
        f.setRE(Integer.parseInt(txtRE.getText()));
        f.setNome(txtNome.getText());
        f.setCpf(txtCPF.getText());
        f.setEmail(txtEmail.getText());
        f.setCelular(txtDDD.getText() + txtCelular.getText());
        f.setDataAdmissao(txtDataAdmissao.getText());
        f.setAtuacao((String) selectAtuacao.getSelectedItem());

        tableModel.addRow(f);
    }//GEN-LAST:event_btnCadastrarActionPerformed

