    private void jtFormularioMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jtFormularioMouseClicked
        // TODO add your handling code here:
        
         if(jtFormulario.getSelectedRow() != -1){
             
            txtRE.setText(jtFormulario.getValueAt(jtFormulario.getSelectedRow(),0).toString());
            txtNome.setText(jtFormulario.getValueAt(jtFormulario.getSelectedRow(),1).toString());
            txtCPF.setText(jtFormulario.getValueAt(jtFormulario.getSelectedRow(),2).toString());
            txtEmail.setText((String) jtFormulario.getValueAt(jtFormulario.getSelectedRow(),3));
            txtCelular.setText(jtFormulario.getValueAt(jtFormulario.getSelectedRow(),4).toString());
            txtDataAdmissao.setText(jtFormulario.getValueAt(jtFormulario.getSelectedRow(),5).toString());
            selectAtuacao.setSelectedItem(jtFormulario.getValueAt(jtFormulario.getSelectedRow(),6).toString());
         }
    }//GEN-LAST:event_jtFormularioMouseClicked

